gameSettings.globalCss:
/* Visibility marker */

.visibleToOthers {
  position: relative;
}

.visibleToOthers::after {
  content: "";
  position: absolute;
  top: 29px;
  left: 10px;
  width: 30px;
  height: 30px;
  background-color: black;
  background-image: url("/i/noto-emoji/emoji_u1f441.svg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: 30px 30px;
  border-radius: 6px;
  pointer-events: none;
  z-index: 1000;
}

/* Card transitions */

.transition {
  transition: transform 1000ms
}

.transition.dragging {
  transition: none
}
